[{"fieldname": "print_heading_template", "fieldtype": "Custom HTML", "options": "{% set current_date = frappe.utils.now_datetime().strftime("%Y-%m-%d") %}
<div class="print-heading">				
	<h2>Secturasoft Feedback List</h2>
	<p>Date: {{current_date}} </p>
</div>"}, {"fieldtype": "Section Break", "label": ""}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "
{% set current_date = frappe.utils.now_datetime().strftime("%Y-%m-%d") %}
<div class="print_head">				
	<h2>Secturasoft Feedback List</h2>
	<p>Date: {{current_date}} </p>
</div>
{% set records = frappe.get_all("Feedback", fields=["project_manager_or_supervisor", "client", "project_name", "resource_name", "rating"]) %}
<style>
  table {
    border-collapse: collapse;
  }
  th, td {
    border: 1px solid black;
    padding: 10px;
    text-align: left;
  }
  th{
      font-weight:bold !important;
      color:black !important;
  }
</style>

<div>

<table >
  <tr>
      <th>SNo.</th>
    <th>Project Manager</th>
    <th>Client</th>
    <th>Project Name</th>
    <th>Resource Name</th>
    <th>Feedback</th>
  </tr>
  {% for index, record in enumerate(records) %}
      <tr>
        <td>{{ index + 1 }}</td>
        <td>{{ record.project_manager_or_supervisor }}</td>
        <td>{{ record.client }}</td>
        <td>{{ record.project_name }}</td>
        <td>{{ record.resource_name }}</td>
        <td>{{ record.rating }}</td>
      </tr>
    {% endfor %}
</table>
</div>"}]
