client_script:
frappe.web_form.after_load=()=>{
		var grid=frappe.web_form.fields_dict['feedback'].grid;
		for( var i=0;i<5;i++){
			var row=grid.add_new_row(i);
		};
		var grid1=frappe.web_form.fields_dict['goals'].grid;
		for( var i=0;i<2;i++){
			var row=grid1.add_new_row(i);
		};

};


//Change form title
 $('.ellipsis h1').text('Feedback Form');


var inputElement = $('input[type="text"]');
inputElement.attr('placeholder', 'Please Enter name');

var selectElement = $('select[type="text"]');
// create a new option element for the placeholder
var placeholderOption = $('<option>', {
  value: 'Please Choose...',
  text: 'Please Choose...',
  disabled: true,
  selected: true
});
// prepend the placeholder option to the select element
selectElement.prepend(placeholderOption);

// Rename save button to submit
$('.web-form-footer .submit-btn').html('Submit');

custom_css:
/* Hide some  elements */
.grid-shortcuts{
    display:none;
}
.web-footer {
    visibility: hidden;
}

.navbar-light {
    visibility: hidden;
    padding-top: 0px;
    padding-bottom: 0px;
    width: 0px;
    height: 0.8px;
}


/* change the width if the input text boxes*/
.frappe-control[data-fieldtype=Select] .control-input {
    width: 50%;
}

.frappe-control[data-fieldtype=Data] .control-input, .control-value {
    width: 50%;
}


/* change  borders,color and title position */
[data-doctype="Web Form"] .page-content-wrapper .container .page_content .web-form-header {
    border: 3px solid #d2e8e6;
    border-top-left-radius: 25px;
    border-top-right-radius: 25px;
    border-bottom-width: 0px;
}

[data-doctype="Web Form"] .page-content-wrapper .container .page_content h1 {
    text-align: center;
    border-radius: 20px;
    background-color: #d2e8e6;
}

[data-doctype="Web Form"] .page-content-wrapper .container .page_content .web-form-header .web-form-head .title {
    display: block;
    justify-content: center;
}

[data-doctype="Web Form"] .page-content-wrapper .container .page_content .web-form {
    border: 3px solid #d2e8e6;
    border-bottom-left-radius: 25px;
    border-bottom-right-radius:25px;
    border-top-width: 0px;
}

.form-grid {
    border: 2px solid #d2e8e6;
}

.grid-heading-row {
    background-color: #d2e8e6;
    border-bottom:2px solid #d2e8e6;
}

/* save and discard button geeting to middle of the page*/

[data-doctype="Web Form"] .page-content-wrapper .container .page_content .web-form .web-form-footer .web-form-actions .left-area {
    display: flex;
    flex: 0;
}

[data-doctype="Web Form"] .page-content-wrapper .container .page_content .web-form .web-form-footer .web-form-actions .right-area {
    display: flex;
    justify-content: center;
    flex: 1;
    flex-direction: row;
    align-content: center;
}


/* Changing all headers to bold */

.web-form-title {
    font-weight: bold;
}

.grid-heading-row {
    font-weight: bold;
}

label {
    font-weight: bold;
}

.form-control.bold {
    font-weight: normal;
}

/* change the bg color of the input boxes */
[data-doctype="Web Form"] .page-content-wrapper .container .page_content .web-form .web-form-wrapper .form-control {
    background-color: #EEF0F2;
}

.like-disabled-input {
    background-color: #F4F5F6;
}

